function setCookie(cname, cvalue, exdate){var d=new Date(); d.setTime(d.getTime() + (exdate*24*60*60*1000)); var expires="expires="+d.toUTCString(); document.cookie=cname + "=" + cvalue + "; " + expires;}function getCookie(cookiename){var name=cookiename + "="; var ca=document.cookie.split(';'); for(var i=0; i<ca.length; i++){var c=ca[i]; while (c.charAt(0)==' ') c=c.substring(1); if (c.indexOf(name)==0) return c.substring(name.length,c.length);}return "";}function checkCookiesEnables(){if(!navigator.cookieEnabled){window.location.href="/cookies/nocookiesenabled.html";}}function understandcookies(){setCookie("understandcookie", "true", "infinite"); $('#cookieinfo').slideUp();}